mapper.select:
SELECT uuid as uuid FROM gsivr.ax_ivr_deploymng WHERE 1=1
AND uuid = #{uuid}
AND step IN ('1','2','3') ORDER BY seq desc

SELECT uuid as uuid, filename as filename, filesize as filesize, crt_dt as crt_dt, crt_by as crt_by, upt_dt as upt_dt, upt_by as upt_by FROM gsivr.ax_ivr_deployfile WHERE 1=1
AND uuid = #{uuid}
ORDER BY filename desc

mapper.update:
UPDATE	gsivr.ax_ivr_deployfile
SET		filename = #{filename, jdbcType=VARCHAR},
		upt_dt = #{upt_dt},
		upt_by = #{upt_by}
WHERE	uuid = #{uuid}
