mapper.select:
SELECT DISTINCT seq as seq, 'SKILL' as stat_id, colname as colname, asname as asname, hanname as hanname, gubun as gubun, type as type, stype as stype, interval as interval FROM WDM.AX_SKILL_FACTOR
WHERE seq = #{seq}
ORDER BY seq

SELECT DISTINCT seq as seq, 'AGENT' as stat_id, colname as colname, asname as asname, hanname as hanname, gubun as gubun, type as type, stype as stype, interval as interval FROM WDM.AX_AGENT_FACTOR
WHERE seq = #{seq}
ORDER BY seq

mapper.update:
UPDATE	WDM.AX_SKILL_FACTOR
SET		hanname = #{hanname},
		gubun = #{gubun},
		type = #{type},
		stype = #{stype},
		asname = #{asname},
		colname = #{colname},
		interval = #{interval}
WHERE	seq = #{seq}
AND		dispname = #{dispname}

UPDATE	WDM.AX_AGENT_FACTOR
SET		hanname = #{hanname},
		gubun = #{gubun},
		type = #{type},
		stype = #{stype},
		asname = #{asname},
		colname = #{colname},
		interval = #{interval}
WHERE	seq = #{seq}
AND		dispname = #{dispname}

mapper.delete:
DELETE	
FROM	WDM.AX_SKILL_FACTOR
WHERE	seq = #{seq}
AND		colname = #{colname}

DELETE	
FROM	WDM.AX_AGENT_FACTOR
WHERE	seq = #{seq}
AND		colname = #{colname}

		DELETE	
		FROM	WDM.AX_USER_FACTOR
		WHERE	stat_gubun = #{stat_gubun}
    	AND		stat_seq = #{stat_seq}

mapper.select:
SELECT	(ROWNUM - 1) AS rm, A.USER_ID AS user_id, A.stat_gubun as stat_gubun, A.stat_seq as stat_seq, A.stat_yn as stat_yn, A.sort as sort, A.dispname as dispname
FROM	(SELECT USER_ID AS user_id, stat_gubun as stat_gubun, stat_seq as stat_seq, stat_yn as stat_yn, sort as sort, dispname as dispname
FROM	WDM.AX_USER_FACTOR
WHERE	user_id = #{user_id}

AND		stat_gubun = #{stat_gubun}


AND		dispname = #{dispname}

order by sort) A
order by rm

mapper.update:
		UPDATE	WDM.AX_USER_FACTOR
    	   SET	sort = #{stat_nseq},
    	   		stat_yn = #{stat_yn}
    	 WHERE	user_id = #{user_id}   
    	 AND	stat_gubun = #{stat_gubun}
    	 AND	stat_seq = #{stat_oseq} 
    	 AND	dispname = #{dispname}

mapper.select:
SELECT	seq as seq, 'SKILL' as stat_id, colname as colname, asname as asname, hanname as hanname, gubun as gubun, type as type, stype as stype, use_yn as use_yn, dispname as dispname, sgroup as sgroup, interval as interval
FROM	WDM.AX_SKILL_FACTOR
WHERE	1=1

AND		dispname = #{dispname}


AND		seq = #{seq}


AND		use_yn = #{useyn}

ORDER BY seq

SELECT	seq as seq, 'AGENT' as stat_id, colname as colname, asname as asname, hanname as hanname, gubun as gubun, type as type, stype as stype, use_yn as use_yn, dispname as dispname, sgroup as sgroup, interval as interval
FROM	WDM.AX_AGENT_FACTOR
WHERE	1=1

AND		dispname = #{dispname}


AND		seq = #{seq}


AND		use_yn = #{useyn}

ORDER BY seq

mapper.update:
