AND		dispname = #{dispname}

mapper.select:
SELECT	CODE, NAME, DATA1, DATA2, REMARK
FROM	AX_COMMON_CODE_M
WHERE	GROUP_CD = #{group_cd}
AND		USE_YN = 'Y'
AND		CODE = #{codeSelect}
AND		REMARK = #{comSelect}
order by SORT
