mapper.select:

SELECT	${select}
FROM 	${from}
WHERE	1=1

AND		row_date BETWEEN #{s_day} AND #{e_day}


AND		starttime BETWEEN #{s_time} AND #{e_time} 


and 	row_date not in (${holday})


and 	TO_CHAR(to_date(row_date,'YYYY-MM-DD') ,'d') not in (#{sat})


and 	TO_CHAR(to_date(row_date,'YYYY-MM-DD') ,'d') not in (#{hol})


and 	row_date in (${hdtday})


and 	TO_CHAR(to_date(row_date,'YYYY-MM-DD') ,'d') in (#{hdt})


and		company_cd = #{compId}
 

and		dept_cd = #{partId}
 

and		team_cd = #{teamId}


and		LOGID in (${agid})


and		AGENT_NAME in (${agname})
	

and		LOGID in (${agids})


and		skill_dbid in (${skId})


GROUP BY ${groupby}

ORDER BY ${orderby}


SELECT	${sum}
FROM 	${from}
WHERE	1=1

AND		row_date BETWEEN #{s_day} AND #{e_day}


AND		starttime BETWEEN #{s_time} AND #{e_time} 


and 	row_date not in (${holday})


and 	TO_CHAR(to_date(row_date,'YYYY-MM-DD') ,'d') not in (#{sat})


and 	TO_CHAR(to_date(row_date,'YYYY-MM-DD') ,'d') not in (#{hol})


and 	row_date in (${hdtday})


and 	TO_CHAR(to_date(row_date,'YYYY-MM-DD') ,'d') in (#{hdt})


and		company_cd = #{compId}
 

and		dept_cd = #{partId}
 

and		team_cd = #{teamId}


and		AGENT_ID in (${agid})


and		AGENT_NAME in (${agname})
	

and		AGENT_ID in (${agids})


and		skill_dbid in (${skId})

order by 1
