mapper.select:
SELECT TO_CHAR(TO_DATE('1970-01-01', 'yyyy-mm-dd') + ((stimets + 32400) / 86400), 'YYYY-MM-DD') as row_date, substr(TO_CHAR(TO_DATE('1970-01-01', 'yyyy-mm-dd') + ((stimets + 32400) / 86400), 'hh24miss'),0,2) || ':' || substr(TO_CHAR(TO_DATE('1970-01-01', 'yyyy-mm-dd') + ((stimets + 32400) / 86400), 'hh24miss'),3,2) || ':' || substr(TO_CHAR(TO_DATE('1970-01-01', 'yyyy-mm-dd') + ((stimets + 32400) / 86400), 'hh24miss'),5,2) starttime, substr(TO_CHAR(TO_DATE('1970-01-01', 'yyyy-mm-dd') + ((ttimets + 32400) / 86400), 'hh24miss'),0,2) || ':' || substr(TO_CHAR(TO_DATE('1970-01-01', 'yyyy-mm-dd') + ((ttimets + 32400) / 86400), 'hh24miss'),3,2) || ':' || substr(TO_CHAR(TO_DATE('1970-01-01', 'yyyy-mm-dd') + ((ttimets + 32400) / 86400), 'hh24miss'),5,2) endtime, b.company_name as com_name, b.dept_name as dept_name, b.team_name as team_name, a.connidfst as connidfst, a.connid as connid, decode(a.calltypeext,1,'Internal',2,'Inbound',3,'Outbound',4,'Consult',null) as calltype, decode(a.calltypeext,2,a.dnis,null) as dnis, a.otherdn as ani, A.lastdn as lastdn, b.agent_id as agent_id, b.agent_name as agent_name, decode(a.rparty,1,'상담사',2,'고객',null) as rparty from WINK1.callinfo a, (select distinct company_cd, company_name, dept_cd, dept_name, team_cd, team_name, agent_dbid, agent_id, agent_name from WDM.ax_a_struct) b WHERE a.lastperson = b.agent_dbid(+)
AND b.company_cd = #{center}
AND b.dept_cd = #{dept_name}
AND b.team_name = #{team_name}
AND a.etimets between WINK1.K2U(#{s_day}) and WINK1.K2U(#{e_day})
AND a.otherdn in (${ani})
AND b.agent_id in (select b.agent_id from WINK1.callstat s, (select distinct company_cd, company_name, dept_cd, dept_name, team_cd, agent_dbid, agent_id, agent_name from WDM.ax_a_struct) b where s.person = b.agent_dbid AND b.agent_name in (${agentid}) AND s.etimets between WINK1.K2U(#{s_day}) and WINK1.K2U(#{e_day}) )
AND a.connid in (select connid from WINK1.callinfo where connidfst in (select connidfst from WINK1.callinfo where connid = (${connid}) and etimets between WINK1.K2U(#{s_day}) and WINK1.K2U(#{e_day}) ) and etimets between WINK1.K2U(#{s_day}) and WINK1.K2U(#{e_day}) )
ORDER BY stimets desc, connidfst desc
