mapper.select:
SELECT seq as seq, ani as ani, description as description, crt_dt as crt_dt, crt_by as crt_by, upt_dt as upt_dt, upt_by as upt_by, connid as connid FROM givr.ax_ivr_blackconsumer_gr WHERE 1=1
AND ani = #{ani}
ORDER BY ani asc, crt_dt asc

mapper.update:
UPDATE	givr.ax_ivr_blackconsumer_gr
SET		description = #{description, jdbcType=VARCHAR},
		connid = #{connid, jdbcType=VARCHAR},
		upt_dt = #{upt_dt},
		upt_by = #{upt_by}
WHERE	seq = #{seq}
