mapper.select:
SELECT dnis as dnis, dnis_name as dnis_name, dnis_group as dnis_group, dnis_media as dnis_media, dnis_useyn as dnis_useyn, crt_dt as crt_dt, crt_by as crt_by, upt_dt as upt_dt, upt_by as upt_by FROM givr.gr_ax_ivr_dnis WHERE 1=1
AND dnis = #{dnis}
AND dnis_useyn = #{dnis_useyn}
ORDER BY dnis asc
