mapper.select:
SELECT dnis as dnis, useyn as useyn, lc_useyn as lc_useyn, hl_useyn as hl_useyn, ov_useyn as ov_useyn, substr(wr_stime,0,2) || ':' || substr(wr_stime,3,2) || ':' || substr(wr_stime,5,2) as wr_stime, substr(wr_etime,0,2) || ':' || substr(wr_etime,3,2) || ':' || substr(wr_etime,5,2) as wr_etime, substr(lc_stime,0,2) || ':' || substr(lc_stime,3,2) || ':' || substr(lc_stime,5,2) as lc_stime, substr(lc_etime,0,2) || ':' || substr(lc_etime,3,2) || ':' || substr(lc_etime,5,2) as lc_etime, substr(sat_stime,0,2) || ':' || substr(sat_stime,3,2) || ':' || substr(sat_stime,5,2) as sat_stime, substr(sat_etime,0,2) || ':' || substr(sat_etime,3,2) || ':' || substr(sat_etime,5,2) as sat_etime, substr(sun_stime,0,2) || ':' || substr(sun_stime,3,2) || ':' || substr(sun_stime,5,2) as sun_stime, substr(sun_etime,0,2) || ':' || substr(sun_etime,3,2) || ':' || substr(sun_etime,5,2) as sun_etime, substr(hl_stime,0,2) || ':' || substr(hl_stime,3,2) || ':' || substr(hl_stime,5,2) as hl_stime, substr(hl_etime,0,2) || ':' || substr(hl_etime,3,2) || ':' || substr(hl_etime,5,2) as hl_etime, substr(ov_stime,0,2) || ':' || substr(ov_stime,3,2) || ':' || substr(ov_stime,5,2) as ov_stime, substr(ov_etime,0,2) || ':' || substr(ov_etime,3,2) || ':' || substr(ov_etime,5,2) as ov_etime, comp_cd as comp_cd, crt_dt as crt_dt, crt_by as crt_by, upt_dt as upt_dt, upt_by as upt_by FROM gsivr.ax_ivr_worktime WHERE 1=1 AND comp_cd = #{comp_cd}
AND dnis = #{dnis}
AND delyn = 'N'
ORDER BY dnis asc, crt_dt asc

mapper.update:
UPDATE	gsivr.ax_ivr_worktime
SET		delyn = 'Y'
WHERE	dnis = #{dnis}

UPDATE	gsivr.ax_ivr_worktime
SET		delyn = 'N'
WHERE	dnis = #{dnis}
