mapper.select:
SELECT seq as seq, dnis as dnis, sdate as sdate, stime as stime, edate as edate, etime as etime, emer_type as emer_type, ment as ment, comp_cd as comp_cd, crt_dt as crt_dt, crt_by as crt_by, upt_dt as upt_dt, upt_by as upt_by FROM gsivr.ax_ivr_emerment WHERE 1=1 AND comp_cd = #{comp_cd}
AND dnis = #{dnis}
AND sdate = #{sdate}
AND stime = #{stime}
ORDER BY stime asc, dnis asc

SELECT seq as seq FROM (SELECT seq as seq FROM gsivr.ax_ivr_emerment WHERE sdate||stime > #{sdate}||#{stime} AND #{edate}||#{etime} BETWEEN sdate||stime AND edate||etime AND comp_cd = #{comp_cd}
AND dnis IN (#{dnis}, 'ALL')
UNION ALL SELECT seq as seq FROM gsivr.ax_ivr_emerment WHERE #{sdate}||#{stime} BETWEEN sdate||stime AND edate||etime AND #{edate}||#{etime} BETWEEN sdate||stime AND edate||etime AND comp_cd = #{comp_cd}
AND dnis IN (#{dnis}, 'ALL')
UNION ALL SELECT seq as seq FROM gsivr.ax_ivr_emerment WHERE #{sdate}||#{stime} BETWEEN sdate||stime AND edate||etime AND #{edate}||#{etime} > edate||etime AND comp_cd = #{comp_cd}
AND dnis IN (#{dnis}, 'ALL')
) WHERE 1=1
AND seq != #{seq}
