mapper.select:
SELECT seq as seq, dnis as dnis, sdate as sdate, stime as stime, edate as edate, etime as etime, va_yn as va_yn, ment as ment, crt_dt as crt_dt, crt_by as crt_by, upt_dt as upt_dt, upt_by as upt_by FROM givr.gr_ax_ivr_emerment WHERE 1=1
AND dnis = #{dnis}
AND sdate = #{sdate}
AND stime = #{stime}
ORDER BY stime asc, dnis asc

SELECT seq as seq FROM (SELECT seq as seq FROM givr.gr_ax_ivr_emerment WHERE sdate||stime > #{sdate}||#{stime} AND #{edate}||#{etime} BETWEEN sdate||stime AND edate||etime
AND dnis = #{dnis}
UNION ALL SELECT seq as seq FROM givr.gr_ax_ivr_emerment WHERE #{sdate}||#{stime} BETWEEN sdate||stime AND edate||etime AND #{edate}||#{etime} BETWEEN sdate||stime AND edate||etime
AND dnis = #{dnis}
UNION ALL SELECT seq as seq FROM givr.gr_ax_ivr_emerment WHERE #{sdate}||#{stime} BETWEEN sdate||stime AND edate||etime AND #{edate}||#{etime} > edate||etime
AND dnis = #{dnis}
) WHERE 1=1
AND seq != #{seq}
