mapper.select:
SELECT holiday as holiday, hl_useyn as hl_useyn, description as description, crt_dt as crt_dt, crt_by as crt_by, upt_dt as upt_dt, upt_by as upt_by FROM gsivr.ax_ivr_holiday WHERE 1=1 AND comp_cd = #{comp_cd}
AND holiday = ${holiday}
AND hl_useyn = #{hl_useyn}
ORDER BY holiday asc

mapper.update:
UPDATE	gsivr.ax_ivr_holiday
SET		hl_useyn = #{hl_useyn},
		description = #{description, jdbcType=VARCHAR},
		upt_dt = #{upt_dt},
		upt_by = #{upt_by}
WHERE	holiday = #{holiday}
