mapper.select:
SELECT url_nm as url_nm, svr_ip as svr_ip, crt_dt as crt_dt, crt_by as crt_by, upt_dt as upt_dt, upt_by as upt_by, file_rst as file_rst FROM gsivr.ax_ivr_url WHERE 1=1
AND comp_cd = #{comp_cd}
AND url_nm = #{url_nm}
ORDER BY url_nm asc, crt_dt asc

mapper.update:
UPDATE	gsivr.ax_ivr_url
SET		svr_ip = #{svr_ip},
		comp_cd = #{comp_cd, jdbcType=VARCHAR},
		upt_dt = #{upt_dt},
		upt_by = #{upt_by}
WHERE	url_nm = #{url_nm}

UPDATE	gsivr.ax_ivr_url
SET		file_rst = #{file_rst}
WHERE	url_nm = #{url_nm}
AND		svr_ip = #{svr_ip}
