mapper.select:
SELECT	row_date as row_date,

		starttime as starttime,

		comp_cd as comp_cd,
		did as did,
		dnis as dnis,
		nvl(n_incall,0) as n_incall,
		nvl(n_agentreq,0) as n_agentreq,
		nvl(n_privacy,0) as n_privacy,
		nvl(n_blackconsumer,0) as n_blackconsumer
FROM	${table}
WHERE	row_date >= #{startdate}
AND		row_date <= #{enddate}

AND		starttime >= #{starttime}
AND		starttime <= #{endtime}


AND		comp_cd = #{comp_cd}


AND		did = #{did}

ORDER BY row_date asc , starttime asc
