mapper.select:
SELECT	ist.row_date as row_date,

		ist.starttime as starttime,

		ist.comp_cd as comp_cd,
		ist.did as did,
		decode(acs.service_name, acs.service_name, ist.service) as service,
		nvl(n_incall,0) as n_incall,
		nvl(n_agentreq,0) as n_agentreq
FROM 	${table} ist, GSIVR.ARS_CODE_SERVICE acs
WHERE	ist.service = acs.service_code(+)
AND		ist.row_date >= #{startdate}
AND		ist.row_date <= #{enddate}

AND		ist.starttime >= #{starttime}
AND		ist.starttime <= #{endtime}


AND		ist.comp_cd = #{comp_cd}


AND		ist.did = #{did}

ORDER BY ist.row_date asc , ist.starttime asc , ist.did asc
