mapper.select:
SELECT ani as ani, flag as flag, crt_dt as crt_dt, crt_by as crt_by, upt_dt as upt_dt, upt_by as upt_by FROM givr.ax_ivr_blackconsumer
WHERE ani = #{ani}
ORDER BY ani asc, crt_dt asc

mapper.update:
UPDATE	givr.ax_ivr_blackconsumer
SET		flag = #{flag},
		upt_dt = #{upt_dt},
		upt_by = #{upt_by}
WHERE	ani = #{ani}
